package JavaPackage;

public class Variable3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[] = {1,2,3,4,56,7,8};  //integer type array 
		
		String b[] = {"Hello","Java","Selenium"};  //String type array 
		
		System.out.println(a[0]);
		System.out.println(a[3]);
		System.out.println(a[4]);
		System.out.println(b[2]);
		
		//array is type of non primitive datatype
		
		//object can hold any type of primitive datatype
		Object c[]= {1,2,3,"Java","Selenium",'a','b',3>10};
		System.out.println(c[7]);
		java();
		
	}
	
	public static void java() {
	   int z =30;
	   System.out.println(z);
	}
	
}


